 #include  __mlu_entry__ void PoolSumBpKernel(half* output, half* input,
                                    unsigned int channels, int out_height,
                                    unsigned int out_width, int kernel_height,
                                    unsigned int kernel_width, int stride_width,
                                    unsigned int stride_height) {
   __nram__ half a_tmp[INPUT_COUNT];
   __nram__ half b_tmp[OUTPUT_COUNT];
   __memcpy(b_tmp, output, OUTPUT_COUNT * sizeof(half), GDRAM2NRAM);
   __memcpy(a_tmp, input, INPUT_COUNT * sizeof(half), GDRAM2NRAM);
   __bang_sumpool_bp(b_tmp, a_tmp, channels, out_height, out_width,
                     kernel_height, kernel_width, stride_width,
                     stride_height);
   __memcpy(output, b_tmp, OUTPUT_COUNT * sizeof(half), NRAM2GDRAM);
